CREATE FUNCTION public.combined_update_scope_items() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    new_labor_unit_quantity REAL;
    new_labor_unit_cost NUMERIC;
    new_labor_unit_type TEXT;
    new_labor_profit_markup REAL;
BEGIN
    IF NEW.task_id IS NOT NULL THEN
        SELECT tasks.unit_quantity, tasks.base_unit_cost, tasks.unit_type, tasks.base_markup_percentage
        INTO new_labor_unit_quantity, new_labor_unit_cost, new_labor_unit_type, new_labor_profit_markup
        FROM tasks
        WHERE tasks.id = NEW.task_id;

        NEW.labor_unit_quantity := COALESCE(NEW.labor_unit_quantity, new_labor_unit_quantity);
        NEW.labor_unit_cost := COALESCE(NEW.labor_unit_cost, new_labor_unit_cost);
        NEW.labor_unit_type := COALESCE(NEW.labor_unit_type, new_labor_unit_type);
        NEW.labor_profit_markup := COALESCE(NEW.labor_profit_markup, new_labor_profit_markup);

        IF NEW.labor_unit_quantity IS NOT NULL AND NEW.labor_unit_cost IS NOT NULL THEN
            NEW.labor_cost_total := NEW.labor_unit_quantity * NEW.labor_unit_cost;
        END IF;

        IF NEW.labor_cost_total IS NOT NULL AND NEW.labor_profit_markup IS NOT NULL THEN
            NEW.labor_profit_total := NEW.labor_cost_total * (1 + NEW.labor_profit_markup / 100);
        END IF;

        IF NEW.labor_profit_total IS NOT NULL AND NEW.labor_cost_total IS NOT NULL THEN
            NEW.labor_client_price := NEW.labor_profit_total + NEW.labor_cost_total;
        END IF;
    END IF;

    NEW.total := COALESCE(NEW.labor_client_price, 0) + COALESCE(NEW.product_client_price, 0);

    -- Other conditions and updates...

    RETURN NEW;
END;
$$;


