CREATE FUNCTION public.jsonb_diff(jsonb1 jsonb, jsonb2 jsonb) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
DECLARE
    result JSONB := '{}'::JSONB;
    key TEXT;
BEGIN
    FOR key IN (
        SELECT DISTINCT jsonb_object_keys(jsonb1) FROM jsonb_each(jsonb1)
        UNION
        SELECT DISTINCT jsonb_object_keys(jsonb2) FROM jsonb_each(jsonb2)
    )
    LOOP
        IF jsonb1 -> key <> jsonb2 -> key THEN
            result := result || jsonb_build_object(key, jsonb_build_object('old', jsonb1 -> key, 'new', jsonb2 -> key));
        END IF;
    END LOOP;
    RETURN result;
END;
$$;


